ITEM.ONLOSTFOCUS:
"{
          if ( #BEG_DATE > #END_DATE )
          {
              showmsg('终止日期应大于开始日期!');
              return(false);
          }
      }"

ITEM.ONCLICK:
    "{
        dim @tota as string; 
        dim @filename as string; 
        initfd();
	      commsend_001();
	      $FD16=#TXNO;
	      $FD21=#PAY_TYPE;
	      $FD64=#TX_BR_NO;
	      $FD59=#FIN_CODE;
	      $FD67=#HST_TYPE;
	      $FD45=#BEG_DATE;
	      $FD44=#END_DATE;

	      @tota=calltips();
        if( $FD12!='0000')
        {
            showmsg(geterror());
  		      return(false);
        }
       //showmsg('查询成功');
        if(autolist(@tota))
       {
           list_work();
       }

