ITEM.ONLOSTFOCUS:
"{
	initfd();
	commsend_001();
	$FD16 = '1307';
	$FD18 = 'SNB';
	$FD75 = #YBWBS;
	$FD72 = '1';
	callagn_ibps();
	if($FD12 != '0000')
	{
		showmsg(geterror());
		return(false);
	}
	#AC_BR_NO = $FD63;
	enable(#AC_BR_NO, false);
	#BR_NAME = $FD118;
	enable(#BR_NAME, false);
	#PAY_AC_NO = $FD25;
	enable(#PAY_AC_NO, false);
	#PAY_NAME = $FD81;
	enable(#PAY_NAME, false);
	#CASH_AC_NO = $FD83;
	enable(#CASH_AC_NO, false);
	#CASH_NAME = $FD113;
	enable(#CASH_NAME, false);
	#TX_AMT = val($FD39,0.01);
	enable(#TX_AMT, false);
	showblock('INFO',true,true);
}"

ITEM.ONCLICK:
"{
	initfd();
	commsend_001();
	$FD16 = '1307';
	$FD18 = 'SNB';
	$FD75 = #YBWBS;
	$FD95 = #REMARK;
	callagn_ibps();
	if($FD12 != '0000')
	{
		showmsg(geterror());
		return(false);
	}
	if (delspace($FD36) == 'PR09')
	{
		showmsg($FD95);
	}
	else
	{
		showmsg('撤销成功');
	}
	return(true);
}"
