				if(len(delspace(#ACNO)) != 0 and substr(#ACNO,0,1)!='5')
				{
					showmsg('请输入对公账号!');
    	    return(false);
				}
		}"

"{
		if (len(delspace(#ACNO)) != 0 )
		{
			if (len(delspace(#LDATE)) != 0)
			{
				showmsg('已输入账号，日期不用再次输入！');
				return(false);
			}
		}
}"

"{

		if (len(delspace(#ACNO)) != 0 )
		{
			if (len(delspace(#HDATE)) != 0)
			{
				showmsg('已输入账号，日期不用再次输入！');
				return(false);
			}
		}
		if (len(delspace(#LDATE)) != 0 and len(delspace(#HDATE)) != 0)
		{
			if (#HDATE < #LDATE)
			{
				showmsg('起始日期不能晚于终止日期！');
				return(false);
			}
		}
}"
