TRANSACTION.COMMSEND:
"{
initfd();
$FD44=#iSDate;//起始日期
$FD45=#iEDate;//结束日期
$FD20=#iBrNo; //交易机构
commsend_001();
}"

TRANSACTION.COMMRECV:
"{
      commrecv_001();
      //金额
      setitems('#xsignbal', $FD39); 
          setitems('#xcash1bal',$FD40); 
          setitems('#xcash2bal',$FD41); 
          setitems('#xcash3bal',$FD42);  
          setitems('#xcash4bal',$FD43); 
          setitems('#xcash5bal',$FD58);
          setitems('#xcash6bal',$FD59);
      //笔数
      //#SEQNO=substr($FD103,130,4);

          #signcnt = substr($FD44,0,8);
          #cash1cnt = substr($FD45,0,8);
          #cash2cnt = substr($FD46,0,8);
          #cash3cnt = substr($FD47,0,8);
          #cash4cnt = substr($FD52,4,8);
          #cash5cnt = substr($FD53,2,8);
          #cash6cnt = substr($FD54,2,8);
          #BRNAME = substr($FD25,0,40);
}"

RESPONSE.PRINT:
"{
  print(02,22,'银行本票业务 [5]机构 统计结果',#iBrNo);
  print(04,10,'起始日期:[8]     终止日期:[8]',#iSDate,#iEDate);
  print(06,08,'-----------------------------------------------------');
  print(07,10,'本行录入:      金额:[20][             8]笔',#xxcash5bal,#cash5cnt);
  print(08,10,'本行签发:      金额:[20][             8]笔',#xxsignbal ,#signcnt );
  print(09,10,'他行兑付:      金额:[20][             8]笔',#xxcash2bal,#cash2cnt);
  print(10,10,'本行兑付:      金额:[20][             8]笔',#xxcash6bal,#cash6cnt);
  print(11,10,'本行挂失:      金额:[20][             8]笔',#xxcash1bal,#cash1cnt);
  print(12,10,'本行退票:      金额:[20][             8]笔',#xxcash3bal,#cash3cnt);
  print(13,10,'本票解付:      金额:[20][             8]笔',#xxcash4bal,#cash4cnt);
}"
