TRANSACTION.COMMSEND:
"{
    initfd();
    commsend_001();
    $FD72=#iPkgInd;
    $FD71=#LW_IND;
    $FD44=#SDATE;   //起始日期
    $FD45=#EDATE;   //结束日期
    $FD91=#BR_NO;
}"

ITEM.ONLOSTFOCUS:
"{
     dim @selid         as number;
     dim @pkgno         as string;
     dim @pkgnoname     as string;
     dim @orbrno        as string;
     dim @orderno       as string;
     dim @acbrno        as string;
     dim @indtee        as string;
     dim @wtdtee        as string;
     dim @packdate      as string;
     dim @txamt         as string;
     dim @tx_amt         as string;
     dim @payacno       as string;
     dim @payname       as string;
     dim @cashacno      as string;
     dim @cashname      as string;
     dim @yddate       as string;
     dim @paysts       as string;
     dim @brno         as string;
     dim @lvsts        as string;
     dim @pkgid        as string;
     dim @lwps         as string;

     if(#DOLIST == '1')
     {
          @selid=list_work();
          if(@selid < 0)
          {
               list_del();
               rerun();
               return(true);
          }
          @selid=list_getselid();
          if(@selid < 0)
          {
             list_del();
             rerun();
             return(true);
          }
          @pkgno   =rtrim(list_gettext(@selid,0));
          @orbrno  =rtrim(list_gettext(@selid,1));
          @orderno =rtrim(list_gettext(@selid,2));
          @acbrno  =rtrim(list_gettext(@selid,3));
          @indtee  =ltrim(list_gettext(@selid,4));
          @wtdtee  =ltrim(list_gettext(@selid,5));
          @packdate=ltrim(list_gettext(@selid,6));
          @tx_amt   =ltrim(list_gettext(@selid,7));
          @txamt=ltrim(comma(list_gettext(@selid,7)));
          @cashacno=delspace(list_gettext(@selid,8));
          @cashname=delspace(list_gettext(@selid,9));
          @yddate  =rtrim(list_gettext(@selid,10));
          @paysts  =rtrim(list_gettext(@selid,11));
          @brno    =rtrim(list_gettext(@selid,12));
          @lvsts   =rtrim(list_gettext(@selid,13));
          @pkgid   =rtrim(list_gettext(@selid,14));
          @lwps    =rtrim(list_gettext(@selid,15));
          if(@pkgno=='004')
          {
             @pkgnoname='实时借记业务';
          }
          else if(@pkgno=='010')
          {
             @pkgnoname='实时借记回执';
          }

          //银行本票基本信息查询
          open('6351_1.XML','#PKGNO='+@pkgnoname+';#ORBRNO='+@orbrno+';#ORDERNO='+@orderno+';#ACBRNO='+@acbrno+';#INDATE='+@indtee+';#WTDATE='+@wtdtee+';#PACKDATE='+@packdate+';#TXAMT='+@txamt+';#CASHACNO='+@cashacno+';#CASHNAME='+@cashname+';#YDDATE='+@yddate+';#PAYSTS='+@paysts+';#BRNO='+@brno+';#LVSTS='+@lvsts+';#PKGID='+@pkgid+';#LWBS='+@lwps+';');
          showfooter();
          return(true);
     }
     list_del();
     #DOLIST='';
}"

"{
    if($KINBR!='10001' and #BR_NO!=$KINBR)
    {
        showmsg('非清算中心不能处理它行交易');
        return (false);
    }
}"

ITEM.ONCLICK:
"{
    dim @tota as string;
    initfd();
    commsend_001();
    $FD44=#SDATE;   //起始日期
    $FD45=#EDATE;   //结束日期
    $FD91=#BR_NO;
    $FD72=#iPkgInd;
    $FD71=#LW_IND;
    @tota=callserver();
    if($FD12!='0000')
    {
        showmsg(geterror());
        return(false);
    }
    autolist(@tota);    
    #DOLIST='1';
}"
