TRANSACTION.COMMSEND:
"{
    initfd();
    commsend_001();
    $FD92=#date_code;   //节假日代码    
    $FD87=#date_name;   //节假日名称
    $FD46=#beg_date;    //起始日期
    $FD47=#end_date;    //终止日期
}"

ITEM.ONLOSTFOCUS:
"{
     dim @selid         as number;
     dim @date_code     as string;
     dim @date_name     as string;
     dim @beg_date      as string;
     dim @end_date      as string;
     dim @days          as string;
     dim @type          as string;
     dim @filler        as string;
     dim @type1         as string;

     if(#DOLIST == '1')
     {
         @selid=list_work();
         if(@selid < 0)
         {
              list_del();
              rerun();
              return(true);
         }
         @selid=list_getselid();
         if(@selid < 0)
         {
             list_del();
             rerun();
             return(true);
         }

         @date_code=rtrim(list_gettext(@selid,0));
         @date_name=rtrim(list_gettext(@selid,1));
         @beg_date=rtrim(list_gettext(@selid,2));
         @end_date=rtrim(list_gettext(@selid,3));
         @days=rtrim(list_gettext(@selid,4));
         @type=rtrim(list_gettext(@selid,5));
         @filler=rtrim(list_gettext(@selid,6));
         @type1=rtrim(list_gettext(@selid,7));

         //showfooter();
         //return(true);
         open('6355.XML','#date_code='+@date_code+';#date_name='+@date_name+';#beg_date='+@beg_date+';#end_date='+@end_date+';#type='+@type1+';#do_type='+'2;');
	       return(true);
     }
     list_del();
     #DOLIST='';
}"

ITEM.ONCLICK:
"{
      dim @tota as string;
      initfd();
      commsend_001();
      $FD92=#date_code;   //节假日代码    
      $FD87=#date_name;   //节假日名称
      $FD46=#beg_date;    //起始日期
      $FD47=#end_date;    //终止日期
      @tota=callserver();
      if($FD12!='0000'){
          showmsg(geterror());
          return(false);
      }
      autolist(@tota);    
      #DOLIST='1';
}"
