TRANSACTION.COMMSEND:
"{
	initfd();
    $MSGTYPE='03001';
    dim @baktxno as string;
    @baktxno=$TXNO;
    $FD12='4504';
    $TXNO='9986';
    $FD25=#CJHM;
    $FD30=#DFZH;
    $FD31=#CJZH;
    $FD32=#BHZH;
    $FD36=#CJQX;
//    $FD40=getitems('#CJJE');
    $FD40= itoa(val(#CJJE,100),'%016d');
    $FD44=#DQRQ;
    $FD67='1';  //拆出方式同城#CRFS
    $FD73=#HTH;
    $FD72='2';
//    $FD84=getitems('#CJLL');
//    $FD85=getitems('#YQLL');
    $FD84=itoa(val(#CJLL,1000000),'%08d');
    $FD85=itoa(val(#YQLL,1000000),'%08d');
//    $FD120=#DFZH+space(88)+$FD40;   //A016  0300 0400
//    $FD121=#BHZH+space(88)+$FD40;   //A017  0320 0400
//    $FD123=space(90)+'0000000000000000';
    commsend_001();
    callserver();
    $TXNO=@baktxno;
    if($FD12!='0000')
    {
        showmsg(geterror());
        return(false);
    }

    $FD16 = '4504';
    $MSGTYPE='03001';
}"

TRANSACTION.COMMRECV:
"{
    #CJZH  = $FD31;
    #DFZH  = $FD32;
//    delspace($FD41);
//    delspace($FD42);
//    #ZHYE  = val($FD41,0.01);
//    #DZHYE = val($FD42,0.01);
    #ZHYE  = ltrim(comma(itoa(val($FD41,0.01),'%.2f'),16));
    #DZHYE = ltrim(comma(itoa(val($FD42,0.01),'%.2f'),16));

    show(#A1,true);
    show(#A2,true);
    show(#A3,true);

    show(#CJZH,true);
    show(#ZHYE,true);
    show(#DZHYE,true);

}"

