ITEM.ONLOSTFOCUS:
"{
      if(#BIDQFS=='1')
      {
        enable(#ACTNO1,false);
        enable(#ACTNO2,true);
        show(#ACTNO1,false);
        show(#ACTNO2,true);
      }
      if(#BIDQFS=='0')
      {
        enable(#ACTNO1,true);
        enable(#ACTNO2,false);
        show(#ACTNO1,true);
        show(#ACTNO2,false);
      }
      refresh();
    }"

ITEM.ONCLICK:
"{
       dim @tota as string;
       dim @selid as number;
       initfd();
       commsend_001();
	   showmsg('!!!!!!#ACNO='+#ACNO);
       $FD16='6609';             //平台用户交易明细查询接口
