       $FD21='1';                //柜面发起标志
       $FD31=#AC_NO;             //账号

     @tota=callagnpt();
     if($FD12!='0000'){
       showmsg(geterror());
       return(false);
     }
     @tota=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
     if(@tota==''){
       showmsg('未找到该用户记录!');
       rerun();
       return(false);
     }
     autolist(@tota);
     @s
