ITEM.ONCLICK:
"{
      dim @tota as string;
      dim @selid as number;
      initfd();
      commsend_001();
      $FD16='6610';             //平台非资金交易查询接口
      $FD60='AGENT';             //平台交易类型
      $FD28=#TX_STAT;
      $FD29=#TX_TEL;

      @tota=callagnpt();
      if($FD12!='0000'){
        showmsg(geterror());
        return(false);
      }
      @tota=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
      if(@tota==''){
        showmsg('未找到该用户记录!');
        rerun();
        return(false);
      }
      autolist(@tota);
      @selid=list_work();
      rerun();
    }"
