ITEM.ONLOSTFOCUS:
 "{
    if(#OPNO!='0')
    {
    	commsend_001();
 		$FD16='9860';
		$FD62=#PANO;
		callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		#NAME=$FD25;
		#MDATE=$FD45;
		#WDAYS=$FD48;
		#PNNO=$FD60;
		#LIND=$FD68;
		setitems('#PINT',$FD84);
		#NOTYPE=$FD90;
		setitems('#TINT#AMT',$FD100);
		show(#NAME,true);
		show(#PINT,true);
		show(#LIND,true);
		show(#WDAYS,true);
		show(#TINT,true);
		show(#NOTYPE,true);
		show(#PNNO,true);
		show(#AMT,true);
		show(#MDATE,true);
	}
    if(#OPNO=='1')
    {
		enable(#LIND,false);
		enable(#NOTYPE,false);
	}
	else if(#OPNO=='2')
	{
		goitem(#PASSWD,false);
	}
 }"

"{
	if (val($HDATE) >= val(#MDATE))
	{
		showmsg('到期日期必须大于当前日期，请重新输入!');
		return(false);
	}
}"

