TRANSACTION.COMMSEND:
"{
    initfd();
 	$MSGTYPE='03001';
	dim @baktxno as string;
	@baktxno=$TXNO;
	$FD12='4935';
	$TXNO='9986';
	$FD25=#NAME;
	$FD44=#MDATE;
	$FD46=#RDATE;
    $FD63=#PANO;
    $FD67=#ACTYPE;
    $FD84=getitems('#PINT');
    $FD100=getitems('#TINT#AMT');
	commsend_001();
    callserver();
    if($FD12!='0000'){
		showmsg(geterror());
		return(false);
	}
	$MSGTYPE='03001';	
	$FD16='4935';
}"

ITEM.ONLOSTFOCUS:
 "{
    	commsend_001();
 		$FD16='9869';
		$FD63=#PANO;
		callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		#NAME=$FD25;
		#MDATE=$FD44;
		#RDATE=$FD46;
		setitems('#PINT',$FD84);
		setitems('#TINT#AMT',$FD100);
		show(#PANO,true);
		show(#NAME,true);
		show(#PINT,true);
		show(#MDATE,true);
    	show(#RDATE,true);
		show(#AMT,true);
		show(#TINT,true);
 }"

