TRANSACTION.COMMSEND:
"{
		initfd();
		$MSGTYPE='03001';
		dim @baktxno as string;
		@baktxno=$TXNO;
		$FD12='4945';
		$TXNO='9986';
		$FD30=#RHZBJZH;
		$FD44=#ZTXRQ;
		$FD46=#HGRQ ;
		$FD62=#XYBH ;
		$FD84=itoa(#ZTXLV*1000000, '%08.0f');
//		$FD84=itoa(val(#ZTXLV,1000000),'%08d');
//		$FD100=getitems('#KKKK#HZPJJE');
		$FD100=getitems('#ZTXLX#HZPJJE');
		commsend_001();
		callserver();
		if($FD12!='0000')
		  {
			   showmsg(geterror());
			   return(false);
		  }
		$FD16='4945';
		$MSGTYPE='03001';
	}"

ITEM.ONLOSTFOCUS:
  "{
  	enable(#ZTXRQ , true);
	enable(#HGRQ  , true);
	enable(#HZPJJE, true);

	$TXNO='9883';
	$FD62=#XYBH;

	commsend_001();
	callserver();
	if($FD12!='0000'){
		showmsg(geterror());
		return(false);
	}

	#ZTXRQ =$FD44;
	#HGRQ  =$FD46;
	#HZPJJE= val(substr( $FD100, 16, 16), 0.01);

	enable(#ZTXRQ , false);
	enable(#HGRQ  , false);
	enable(#HZPJJE, false);

  }"

