ITEM.ONLOSTFOCUS:
"{
     if(#DQFS=='1')
     {
     enable(#ACTNO1,false);
     enable(#ACTNO2,true);
     show(#ACTNO1,false);
     show(#ACTNO2,true);
     }
     if(#DQFS=='0')
     {
     enable(#ACTNO1,true);
     enable(#ACTNO2,false);
     show(#ACTNO1,true);
     show(#ACTNO2,false);

     }
     refresh();
   }"

  "{
	if(substr(#CARD_NO,0,6)!= '621223' and substr(#CARD_NO,0,6)!= '621780')
	{
		showmsg('卡号的前6位必须为621223或者621780');
		return(false);
	}
	if(len(rtrim(#CARD_NO))!=19)
	{
	   showmsg('卡号长度不正确，请重新输入!');
	   return(false);
	}
