TRANSACTION.COMMRECV:
"{
	 commrecv_001();
         if($FD12!='0000'){
			return(false);}
    showmsg('交易成功!')	
}"

TRANSACTION.FIRSTWORK:
"{
	dim @txno_bak as string;
	dim @tota as string;
	@txno_bak=$TXNO;
	$TXNO='9885';
	commsend_001();
	@tota=callserver();	
    //showmsg(@tota);
	setsel(#PZZL,@tota,'o5t20o1v3o2');
	initfd();
}"

ITEM.ONLOSTFOCUS:
"{
       if(#ZZHM < #QSHM)
       {
         showmsg('终止号码必须大于起始号码');
         return(false);
       }
}"

"{
   // #PZSL=itoa((atoi(substr(#ZZHM,8,8))-atoi(substr(#QSHM,8,8))+1),'%12d'); mod 20120103
   initfd(); 
   commsend_001(); 
   $FD16='9204'; 
   $FD58=#QSHM; 
   $FD59=#ZZHM; 
   callserver(); 
   if($FD12!='0000') 
   { 
        showmsg(geterror()); 
        return(false); 
   } 
   #PZSL=$FD60;  
   show(#PZSL,true);
   enable(#PZSL,false);
}"

