TRANSACTION.COMMSEND:
"{
     dim @txno_bak as string; 
     @txno_bak=$TXNO;
		 $TXNO='9986';
		 $MSGTYPE='03001';
		 $FD12='5303';
		 $FD102=#KZH+space(125-len(#KZH))+#KHMC;
		 commsend_001();
		 callserver();
		 $TXNO=@txno_bak;
		 if($FD12!='0000')
		 {
			showmsg(geterror());
			return(false);
			}

		 $FD16='5303';	
		 $FD102=#KZH+space(125-len(#KZH))+#KHMC;
	   $MSGTYPE='03001';	
	}"

TRANSACTION.COMMRECV:
"{
	commrecv_001();
	if($FD12!='0000')
		{
		showmsg(geterror());
		return(false);
		}
// showmsg('46=['+$FD46+']47=['+$FD47+']');
	 #JF=$FD46;
	 #SYJF=$FD47;
// enable(#JF, false);
// enable(#SYJF, false);
	 showblock('RESULT',true,true);

	 if($FD12 == '0000')
	 {
	 showmsg('交易成功!');
	 }
   }"

ITEM.ONLOSTFOCUS:
   "{
		dim @txno as string;
		$FD102=#KZH;
	  @txno=$TXNO;
		$TXNO='9851';
		commsend_001();
		callserver();
		$TXNO=@txno;
		if($FD12!='0000')
		{
			showmsg(geterror());
			return(false);
		}
    #KHMC=substr($FD102, 125, 14);
		enable(#KHMC,false);
    }"
