TRANSACTION.COMMRECV:
"{
	dim @d as date;
	dim @prtcmd as string;
	dim @mydate as number;
	commrecv_001();
	if($FD12 == '0000')
	 {
		// showmsg('交易成功!');
		@d=$HDATE;
		#MYDATE=@d-1;

		@prtcmd='fview -p rlp'+$KINBR+$NETPRT+' '; 
		if(call('list_rpt_name.sh '+#MYDATE+' '+ $KINBR,1)!=0)
		{
			showmsg('shell程序调用异常');
		}
	//	call('list_rpt_name.sh +@d+' '+ $KINBR',1); 
		call(@prtcmd+'../rpt/'+#MYDATE+'/'+$KINBR+'/'+'filelist.txt'); 
	 }
}"

