ITEM.ONLOSTFOCUS:
    "{
	dim @txno_bak as string;
	dim @tota as string;
	@txno_bak=$TXNO;
	$TXNO='9200';
	$FD92=#JJKH;
	commsend_001();
	@tota=callserver();
	if($FD12 != '0000')
	{
		showmsg(geterror());
		return(false);
	}
	$TXNO=@txno_bak;
     }"

    "{if(#JSKH!=#JJKH)
    {
	dim @txno_bak as string;
	dim @tota as string;
	@txno_bak=$TXNO;
	$TXNO='9200';
	$FD92=#JJKH;
	commsend_001();
	@tota=callserver();
	if($FD12 != '0000')
	{
		showmsg(geterror());
		return(false);
	}
	$TXNO=@txno_bak;
    }
    else
    {showmsg('输入有误');
		return(false);}
		 }"

ITEM.ONCLICK:
"{initfd();
	dim @tota as string;

	commsend_001();

	$FD92=#JJKH;
	$FD91=#JSKH;
	$FD69=#JJZT;
	$FD68=#JSZT;
@tota=callserver();
if($FD12!='0000'){
		showmsg(geterror());
		return(false);
	}
	runoutput();

}"

RESPONSE.PRINT:
"{
	if(#JJZT=='2'){
		print(8,35,'交接成功');
		print(10,35,'启用:[4]',#JJKH);
		print(12,35,'锁定:[4]',#JSKH);}
		else {
		print(20,35,'交接成功');
		print(22,35,'锁定:[4]',#JJKH);
		print(24,35,'启用:[4]',#JSKH);
		}
	}"
