TRANSACTION.COMMRECV:
"{
	commrecv_001();

	 if($FD68=='1')
	 {
	  showmsg('柜员'+$TLRNO+'为最后一个签退柜员,请做网点签退!');
	 }
	 if($FD12 == '0000')
	 {
		 showmsg('签退成功!');
	 }
}"

