TRANSACTION.COMMSEND:
   "{
    //与神码通讯开始 
    dim @filename as string;
    dim @shuju as string;
    dim @update as string;
    @filename='../tmp/'+$FD7+$FD10+'.fds';
    savefds(@filename);
    if(#VOCTYPE=='020')
    { 
      initfd();
      commsend_001();
      $FD16='7707';
      #ACTNO='6217800109002989';
      $FD30=#ACTNO;
      $FD75=#MSR2BAK;
      $FD76=#MSR3BAK;     
      $FD21=#CURCD1;  //$FD21=substr($FD101,140,2);  当前默认只识别RMB
      $FD39= itoa(val(#LTXAMT,100),'%016.2f');  // 交易金额
      $FD42=itoa(val(#ICLAVBAL,100),'%016.2f');    //电子现金卡上的余额
      $FD79=#PASSWORD; 
      // 应用交易计数器 IC_ATC 和 应用密文 IC_AC
      @shuju= initiccard();
      if(strfld(@shuju,'|',0)!=9000')
      {
      	showmsg('函数initiccard返回值有误!');
      	return(false);
      }
      #ICJSQ=strfld(@shuju,'|',1); 
      #ICMW=strfld(@shuju,'|',2);
      #ICSEQID=strfld(@shuju,'|',3); 
      showmsg('应用JSQ='+#ICJSQ);       
      showmsg('应用密文#ICMW='+#ICMW);
      showmsg('IC卡序列号#ICSEQID='+#ICSEQID);
			$FD35=#ICSEQID; //三位IC卡序列号
      $FD36=#ICJSQ;
      $FD59=#ICMW;
      $FD43=#ORSEQNO;//原交易流水
      if(chkiccard()!='0000')
        {
          showmsg('往神码发交易前，不允许拔卡!');
          //goitem('#ACTNO');
          return(false);
        }
      callagn();
      if($FD12!='0000')
      {
        showmsg(geterror());
        return(false);
      }
      #CARD_TRACENO=delspace(itoa(val($FD43),'%16d'));  

      showmsg('报错开始!');
      @update=updateiccard('04DA9F790a0000000850006F94ABF1,04DC010C42703C9F61123635333132323139383630373135343431349F62010057136217800109002658D30122200004400284000F5F200848414E204348414F9F1F00260D27E7');

    }
    initfd();
    commsend_001();
    loadfds(@filename);
    //与神码通讯结束
    commsend_001();
    commsend_101();
    $FD118 = #CURCD1+space(19)+space(16,'0');
   }"

TRANSACTION.COMMRECV:
"{
 commrecv_001();
 if(($FD12!='0000') and ($FD12!='Z003') and (substr(#ACTNO,0,6)=='621223'))//卡交易核心处理失败往数码发送取消交易
 {
   #RESP_CODE=$FD12;
   showmsg(geterror());
   initfd();
   commsend_001();
   $FD16='7777';
   $FD12=#RESP_CODE;
   $FD30=#ACTNO;
   $FD39= itoa(atoi(#LTXAMT)*100,'%016f'); 
   $FD43=#CARD_TRACENO;
   callagn();
   if($FD12!='0000')
   {
     showmsg(geterror());
     return(false);
   }
   showmsg('现金充值失败!!...');
   rerun();
 }   
