ITEM.ONLOSTFOCUS:
"{
		     if(#DQFS=='0')
		     {
			     enable(#ACNO_CT,false);
			     enable(#ACNO_IC,true);
			     show(#ACNO_IC,true);
			     show(#ACNO_CT,false);
		     }
		     if(#DQFS=='1')
		     {
			     enable(#ACNO_IC,false);
			     enable(#ACNO_CT,true);
			     show(#ACNO_CT,true);
			     show(#ACNO_IC,false); 
		     }
		   }"

        "{
        	if(#DQFS=='0')
					{
						#ACNO=#ACNO_IC;
					}
					if(#DQFS=='1')
					{
						#ACNO=#ACNO_CT;
					}
					
              initfd();
              commsend_001();
              $FD16='2315';
              $FD30=#ACNO;
              $FD68=2;
              callserver();
              if($FD69 == '0' and $FD70 !=  '*')
              {
                  showmsg('当前账户未签约');
                  return(false);
              }
              if($FD12 != '0000')
              {
                  showmsg(geterror());
                  return(false);
              }
        }"

"{
    if(#KSRQ > #JSRQ and  #JSRQ != '')
    {
        showmsg('开始日期不能大于结束日期');
        return(false);
    }
}"

"{
	dim @f as string;
	initfd();
	commsend_001();
	$FD16='2314';
	$FD31=#ACNO;
	$FD67=#ZHZT;
	$FD68=#SFBZ;
	$FD44=#KSRQ;
	$FD45=#JSRQ;
	@f=callserver();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	$TXNO='2314';
	if(autolist(@f))
	{
		list_work();
		refresh();
	}
}"
