ITEM.ONLOSTFOCUS:
"{
		     if(#DQFS=='0')
		     {
			     enable(#ACNO_CT,false);
			     enable(#ACNO_IC,true);
			     show(#ACNO_IC,true);
			     show(#ACNO_CT,false);
		     }
		     if(#CHILD_DQFS=='1')
		     {
			     enable(#ACNO_IC,false);
			     enable(#ACNO_CT,true);
			     show(#ACNO_CT,true);
			     show(#ACNO_IC,false); 
		     }
		   }"

      "{
      	if(#DQFS=='0')
				{
					#AC_NO=#ACNO_IC;
				}
				if(#DQFS=='1')
				{
					#AC_NO=#ACNO_CT;
				}
      	
	      commsend_001();
	      $FD31=#AC_NO;
	      $FD16='9958';
	      callserver();
	      if($FD12 != '0000')
	      {
	          showmsg(geterror());
	          return(false);
	      }
	      #AC_NO=delspace($FD31);
	      
	      commsend_001();
	      $FD16='9331';
	      $FD102=getitems('#AC_NO');
	      callserver();
	      if($FD12 != '0000')
	      {
	          showmsg(geterror());
	          return(false);
	      }
      }"

"{
	dim @f as string;
	
	initfd();
	commsend_001();
	$FD16='2319';
	$FD30=#AC_NO;
	//$FD44=#KSRQ;
	//$FD45=#JSRQ;
	@f=callserver();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	$TXNO='2319';
	if(autolist(@f))
	{
		list_work();
		refresh();
	}
}"
