TRANSACTION.FIRSTWORK:
"{
	dim @txno_bak as string;
	dim @tota as string;
	dim @list1 as string;
	dim @list2 as string;
	dim @i as number;

	@txno_bak=$TXNO;
	$TXNO='9966';
	commsend_001();
	@tota=callserver();
	@i = 0;
	while('0230' == substr(@tota, @i, 4)){
		@i = @i + 30;
	}
	@list1=substr(@tota, 0 , @i+1);
	@list2=substr(@tota, @i, len(@tota)-@i);
	setsel(#DSCPTID,@list1,'o5t20o1v2o2');
	setsel(#VOCTYPE,@list2,'o5t20o1v3o2');
//showmsg('33');
//	$FD11='';
}"

ITEM.ONLOSTFOCUS:
"{
	enable(#ACTNO, true);
	enable(#TRANTYP, true);
	if( '7' == #ACTTYPE ){
		#ACTNO='';
		#TRANTYP = '1';
		enable(#ACTNO, false);
		enable(#TRANTYP, false);
	}
	if(#ACTTYPE=='1')
	{
	   setsel(#TRANTYP,'转帐|2|单边|5|','t4o1v1o1');
	}else{
	   setsel(#TRANTYP,'现金|1|转帐|2|单边|5|','t4o1v1o1');
	}
}"

	   "{
		dim @txno as string;

		if(#ACTNO=='41600' or substr(#ACTNO,0,3)=='407'){
			showmsg('不可记此科目!');
			return(false);
		}
		if(substr(#ACTNO,0,3)=='408'){
			if($KINBR!='50001'){
				showmsg('不可以记此科目的帐务!');
				return(false);
			}
		}

		$FD30=#ACTNO;
		$FD68=#ACTTYPE;
		@txno=$TXNO;
		$TXNO='9930';
		commsend_001();

		callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		#COUR='01';
		#ACTNM=$FD25;
		#AVBALCRDB=$FD22;
		#AVBAL=val($FD41, 0.01);

		enable(#COUR, false);
		enable(#ACTNM, false);
		enable(#AVBALCRDB, false);
		enable(#AVBAL, false);
  }"

 "{
	if(#AVBALCRDB == '借')
	{
		if(#CRDB == '1')
		{
			#AFBAL = #AVBAL+#TXAMT;
		}
		else
		{
			#AFBAL = #AVBAL-#TXAMT;
		}
	}
	else if(#AVBALCRDB == '贷')
	{
		if(#CRDB == '1')
		{
			#AFBAL = #AVBAL-#TXAMT;
		}
		else
		{
			#AFBAL = #AVBAL+#TXAMT;
		}
	}
	//showmsg(itoa(#AFBAL,'%17.2f'));
 }"

"{
	if('31' != #DSCPTID){
		#DSCPTS = #DSCPT;
		enable(#DSCPTS, false);
	}
	else
	{
		#DSCPTS='';
		show(#DSCPTS,true);
		enable(#DSCPTS, true);
	}

}"

ITEM.ONCLICK:
"{
   if(#CRDB=='1') //借
   {
     loadpage('PAGE2');
   }else if(#CRDB=='2'){ //贷
     loadpage('PAGE3');
   }else{
   	showmsg('借贷标志错误!');
   	return(false);
   }
}"

