ITEM.ONLOSTFOCUS:
"{ dim @txno as string;
   @txno=$TXNO;
   $TXNO='9958';
   commsend_001();
   $FD38=#ZH;
   callserver();
   $TXNO=@txno;

   if($FD12!='0000')
     {showmsg(geterror());
       return(false);
       }
 if(#ZH != '')
   {
       initfd();
       @txno=$TXNO;
       $TXNO='6700';
       commsend_001();
       $FD30=#ZH;
       $FD67='2';
       callserver();
       $TXNO=@txno;     
       if($FD12!='0000')
          {showmsg(geterror());
           return(false);
       }
       if($KINBR!=$FD91)
       {
           showmsg('只能查询本机构帐户!');
           return(false);
       } 
   }  
      }"

ITEM.ONCLICK:
"{
		dim @tota as string;
		dim @selid as number;
		commsend_001();
		$FD16='8125';
		$FD38=#ZH;
		@tota=callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		if(autolist(@tota)){
			@selid=list_work();
			if(@selid >= 0){
			//	showmsg(list_gettext(@selid,1));
			}
		}
	}"
