ITEM.ONLOSTFOCUS:
  "{
	dim @selid as number;

	dim @t10000 as string;
	dim @t5000  as string;
	dim @t2000  as string;
	dim @t1000  as string;
	dim @t500   as string;
	dim @t200   as string;
	dim @t100   as string;
	dim @t50    as string;
	dim @t20    as string;
	dim @t10    as string;
	dim @t5     as string;
	dim @t2     as string;
	dim @t1     as string;

		if(#DOLIST == '1')
		{
			@selid = list_work(); 
			if(@selid < 0)
			{
				list_del();
				rerun();
				return(true);
			}
			@selid = list_getselid();
			if(@selid < 0 or #CERSG == ' ')
			{
				list_del();
				rerun();
				return(true);
			}
		@t10000 = list_gettext(@selid,12);
		@t5000  = list_gettext(@selid,13);
		@t2000  = list_gettext(@selid,14);
		@t1000  = list_gettext(@selid,15);
		@t500   = list_gettext(@selid,16);
		@t200   = list_gettext(@selid,17);
		@t100   = list_gettext(@selid,18);
		@t50    = list_gettext(@selid,19);
		@t20    = list_gettext(@selid,20);
		@t10    = list_gettext(@selid,21);
		@t5     = list_gettext(@selid,22);
		@t2     = list_gettext(@selid,23);
		@t1     = list_gettext(@selid,24);

		open('94011.XML','#T10000='+@t10000+';#T5000='+@t5000+';#T2000='+@t2000+';#T1000='+@t1000+
		';#T500='+@t500+';#T200='+@t200+';#T100='+@t100+';#T50='+@t50+';#T20='+@t20+';#T10='+@t10+';#T5='+@t5+';#T2='+@t2+';#T1='+@t1+';');
		return(true);
		}
	list_del();
	#DOLIST = '';

  }"

ITEM.ONCLICK:
  "{
	dim  @tota as string;

	commsend_001();
	$FD16='8402';
	$FD21=#CUNO;
	$FD29=#LDATE;
	$FD38=#ACNO;
	$FD44=#HDATE;
	$FD63=getitems('#BRF');
	$FD64=#TLNO;
	$FD70=#CERSG;  //现用作券别登记标志
	$FD91=#BRNO;
	$FD100=getitems('#AMT');

	@tota=callserver();
	if($FD12!='0000') 
	{
		showmsg(geterror());
		return(false);
	}

	autolist(@tota);
	#DOLIST = '1';

  }"
