ITEM.ONCLICK:
  "{
	dim @f as file;
	dim @line as string;
	dim @fld0 as string;
	dim @fld1 as string;
	dim @fld2 as string;
	dim @tota as string;
	dim @selid as number;
  	commsend_001();
	$FD123='1=1 ';
	@tota=callserver();
	savefiletxt('../tmp/'+$KINBR+$TTYNAME,@tota);

    	if($FD12!='0000')
    	{
    		showmsg(geterror());
    		return (false);
    	}
	@f=openfile('../tmp/'+$KINBR+$TTYNAME);
	if(@f < 0){
		showmsg('打开回传文件错误就');
		return(false);
	}
	list_init(17,78,3,1);
	list_setcolcnt(3);
	list_setcol(0,'参与者',13);
	list_setcol(1,'权限种类',10);
	list_setcol(2,'权限内容',50);
	while(true){
		@line=readline(@f);
		if(@line!=''){
			@fld0=strfld(@line,'|',0);
			@fld1=strfld(@line,'|',1);
			@fld2=strfld(@line,'|',2);
			list_additem(@fld2,@fld0,@fld1,substr(@fld2,0,45));
		}else{
			break;
		}
	}
	while(true){
		@selid=list_work();
		if(@selid < 0){
			break;
		}
		msgbox(list_getaction(@selid)+chr(10)+chr(10)+chr(10)+chr(10)+chr(10)+chr(10)+chr(10)+chr(10)+chr(10),'行号['+list_gettext(@selid,0)+']的'+list_gettext(@selid,1),'ok');
	}	
		list_del();	
	rerun();
   }"
