ITEM.ONLOSTFOCUS:
"{
   initfd();
   commsend_001();
   $FD16='1010';                  //平台用户查询接口
   $FD60='RQDS';                  //平台交易类型
   $FD30=#USER_NO;
   callagnpt();
   if($FD12!='0000')
   {
     showmsg(geterror());
     return(false);
   }
   #YHNAME=delspace($FD25);       //用户姓名
   #ADDRESS=delspace($FD76);      //用户单位及地址
}"

"{
    show(#LMM,false);
    show(#DRAW_PWD,false);

    initfd();
    commsend_001();
    $FD16='9040';            //平台代收签约查询接口
    $FD60='RQDS';            //平台交易类型
    $FD30=#USER_NO;          //用户代码

    callagnpt();
    if($FD12!='0000'){
      showmsg(geterror());
      return(false);
    }
    #AC_NO=delspace($FD31);   //签约账号
    #AG_STS=delspace($FD70);  //代扣签约状态
    #ITEM_NO=delspace($FD63); //个人代理编号

    //核心查询已签约卡户名
    dim @info as string;
    dim @tmpacid as string;
    dim @tmpnotests as string;  //介质状态
    dim @tmpacsts as string;    //账户状态
    dim @tmpholdsts as string;  //冻结状态
    #AC_NAME='';
    #OPN_BR_NO='';
    refresh();

    initfd();
    commsend_001();
    $FD16='9598';
    $FD123='select ac_id, note_sts, opn_br_no from mdm_ac_rel where ac_no='+chr(39)+#AC_NO+chr(39);
    @info=callserver();
    if($FD12!='0000')
    {
        showmsg(geterror());
        return(false);
    }
    if(@info=='')
    {
        showmsg('账号不存在,请核对后重新输入!!');
        return(false);
    }
    @tmpacid=delspace(strfld(@info,'|',0));
    @tmpnotests=delspace(strfld(@info,'|',1));
    #OPN_BR_NO=delspace(strfld(@info,'|',2));

    initfd();
    commsend_001();
    $FD16='9598';
    @info='';
    $FD123='select name, hold_sts, ac_sts from dd_mst where ac_id='+chr(39)+@tmpacid+chr(39);
    @info=callserver();
    if($FD12!='0000')
    {
        showmsg(geterror());
        return(false);
    }
    #AC_NAME=delspace(strfld(@info,'|',0));
    @tmpholdsts=delspace(strfld(@info,'|',1));
    @tmpacsts=delspace(strfld(@info,'|',2));

    //未签约用户进行解约操作
    if(#AG_STS == '1' and #YW_TYPE == '1')
    {
      showmsg('您尚未签约燃气费用代扣业务,无法解约!!');
      return(false);
    }
    //已签约用户进行签约操作
    if(#AG_STS=='0' and #YW_TYPE=='0')
    {
      showmsg('您已经签约燃气费用代扣业务!!');
      return(false);
    }

    //签约时,校验账户状态为正常的可进行;解约时,不校验账户状态
    if((@tmpnotests != '0' or @tmpholdsts != '0' or @tmpacsts != '1') and #YW_TYPE == '0')
    {
      if(@tmpnotests == '1')
      {
        showmsg('该账户已挂失,不允许签约!!');
      }
      if(@tmpacsts == '*')
      {
        showmsg('该账户已销户,不允许签约!!');
      }
      if(@tmpholdsts != '0')
      {
        showmsg('该账户已被冻结,不允许签约!!');
      }
      return(false);
    }

    show(#LMM,true);
    show(#DRAW_PWD,true);
}"

"{
  initfd();
  commsend_001();
  $FD16='9961';             //输入帐号，回显证件信息
  $FD30=#AC_NO;
  callserver();
  if($FD12!='0000'){
    showmsg(geterror());
    return(false);
  }
  #C_CERTNO=delspace($FD62);
  #C_CERTTYPE=delspace($FD67);

  if(#C_CERTNO != delspace(#CERTNO))
  {
      showmsg('输入的证件号有误，与帐户开户时使用的证件号不匹配！');
      return(false);
  }
  if(#C_CERTTYPE != delspace(#CERTTYPE))
  {
      showmsg('输入的证件类型有误，与帐户开户时使用的证件类型不匹配！');
      return(false);
  }

  //add 20131030 wudawei start 调用身份核查
  dim @res as string;
  if(#CERTTYPE=='1' or #CERTTYPE=='2')
  {
    @res =  idcheck(#CERTNO,#AC_NAME);
    #HCJGBL = strfld(@res,'|',1);
    @res =  strfld(@res,'|',0);
    if(@res=='02' or @res=='03' or @res=='04' or @res=='05' or @res=='06')
    {
      showmsg('身份核查结果不通过，不能继续办理！');
      return(false);
