ITEM.ONCLICK:
"{
    initfd();
    commsend_001();
    $FD16='5010';             //平台燃气费用代扣签解约接口
    $FD60='RQDS';             //平台交易类型
    $FD20='1';                //柜面发起标志
    $FD30=#USER_NO;           //用户代码
    $FD31=#AC_NO;             //签约账户
    $FD33=#AC_NAME;           //账户名称
    $FD70=#YW_TYPE;           //业务类型
    $FD63=#ITEM_NO;           //个人代理编号

    //将用户代码对应的姓名、地址信息写入流水记录
    $FD25=#YHNAME;            //用户姓名
    $FD76=#ADDRESS;           //用户单位及地址

    callagnpt();
    if($FD12!='0000'){
      showmsg(geterror());
      return(false);
    }

    #TRACE_NO=delspace($FD32);      //平台流水号
    runoutput();
    rerun();
  }"

