"{
  if(substr(#AC_NO,0,6)!='621223' and substr(#AC_NO,0,6)!='621780')  //存折验密
  {
    dim @baktxno as string;
    @baktxno=$TXNO;
    $TXNO='9317';
    commsend_001();
    $FD30=#AC_NO;
    $FD79=getitems('#DRAW_PWD');
    callserver();
    $TXNO=@baktxno;
    if($FD12!='0000'){
      showmsg(geterror());
      return (false);
    }
  }
  else if(substr(#AC_NO,0,6) == '621223' or substr(#AC_NO,0,6)=='621780')  //卡验密
  {
    initfd();
    commsend_001();
    $FD16='7007';         //卡验密接口
    $FD30=#AC_NO;         //绑定卡号
    $FD68='4';
    $FD79=#DRAW_PWD;      //卡密码
    callagn();
    if( $FD12 != '0000')
    {
      showmsg(geterror());
      return(false);
    }
  }
}"

ITEM.ONCLICK:
"{
  initfd();
  commsend_001();
  $FD16='3369';                   //退费需要授权
  callserver();
  if($FD12 !='0000')
  {
    showmsg(geterror());
    return(false);
  }

  initfd();
  commsend_001();
  $FD16='3010';                   //平台退费接口
  $FD60='RQDS';                   //平台交易类型
  $FD20='1';                      //柜面发起标志
  $FD30=#USER_NO;
  $FD32=#OLD_TRACENO;
  $FD28=#OLD_DATE;
  $FD39=itoa(#OLD_AMT,'%-10.2f'); //交易金额
  $FD79=#DRAW_PWD;

  //退费时，将原交易流水信息写入退费记录
  $FD25=#YHNAME;
  $FD76=#ADDRESS;
  $FD86=#SYBS;
  $FD87=#BYBS;
  $FD88=#YL;
  $FD91=#DJ;
  $FD73=#RQF;
  $FD84=#BXF;
  $FD85=#FZJ;
  $FD58=#SYQF;
  $FD59=#ZNJ;
  $FD26=#SYJY;
  $FD61=#DYJJK;
  $FD52=#YSHJ;
  $FD27=#CHBY;
  $FD62=#PHONE;
  $FD68=#JFFS;
  $FD31=#AC_NO;
  $FD33=#AC_NAME;

  callagnpt();
  if($FD12!='0000')
  {
    showmsg(geterror());
    return(false);
  }

 #TRACE_NO=delspace($FD32);           //平台流水号

  //转账存折缴费场合，需要补登存折
  if(#JFFS=='2' and len(delspace(#AC_NO))==13)
  {
    showmsg('原缴费交易为转账存折方式，请凭证打印完毕后进行3407补登存折！');
  }

 runoutput();
 rerun();
}"

