ITEM.ONCLICK:
"{
      dim @tota as string;
      dim @selid as number;
      initfd();
      commsend_001();
      $FD16='6631';            //供电公司对账完成后生成一个跨行转账信息，此交易通过前台查询来取回文件
      $FD60='AGENT';           //平台交易类型
      $FD21='01';              //柜面发起标志     
      $FD28=#TX_DATE;          //日期
      $FD29=#TX_TEL;

      @tota=callagnpt();
      if($FD12!='0000'){
        showmsg(geterror());
        return(false);
      }
      @tota=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
      if(@tota==''){
        showmsg('未找到记录!');
        rerun();
        return(false);
      }
      autolist(@tota);
      @selid=list_work();
     runoutput();
      rerun();
    }"
