ITEM.ONLOSTFOCUS:
"{
       if(#ZZHM < #QSHM)
       {
         showmsg('终止号码必须大于起始号码');
         return(false);
       }
}"

"{
   // #PZSL=itoa((atoi(substr(#ZZHM,8,8))-atoi(substr(#QSHM,8,8))+1),'%12d'); mod 20120103
   initfd(); 
   commsend_001(); 
   $FD16='9204'; 
   $FD58=#QSHM; 
   $FD59=#ZZHM; 
   callserver(); 
   if($FD12!='0000') 
   { 
        showmsg(geterror()); 
        return(false); 
   } 
   #PZSL=$FD60;
   show(#PZSL,true);
   enable(#PZSL,false);
}"

ITEM.ONCLICK:
    "{ 
	initfd();
      commsend_001();
      $FD16='6615';              //平台凭证入库
      $FD60='AGENT';             //平台交易类型
      $FD21='01';                //柜面发起标志
	$FD58=#QSHM;
	$FD59=#ZZHM;
	$FD52=#PZSL;
	$FD89=#PZZL;

	 callagnpt();
      if($FD12!='0000'){
        showmsg(geterror());
        return(false);
      }

	   runoutput();
      rerun();
    }"

