       "{
	        dim @s as string;
	        dim @tota as string;
	        dim @list1 as string;
	        dim @list2 as string;
	        dim @i as number;
					initfd();
					$TXNO='9954';
					$FD101=#ACTNO;
					commsend_001();
					@tota=callserver();
					if($FD12!='0000')
					{
						showmsg(geterror());
						return(false);
					}
					setitems('#ACTNO24#ACTSEQ#TXAMT#VOCTYPE#VOCNUM#DESC10#NBFLAG#CUSTNAME#AVBAL#CURCD#CASHFLAG#DSCPT#ACTTYPE#ACTSEQ_2#PRODNAME',$FD101);
					show(#LNAME,true);
					show(#LCURCD,true);
					show(#LAVBAL,true);
					show(#LPRODNAME,true);
					show(#LVOCTYPE,true);
					show(#LCASHFLAG,true); //取消转帐
					show(#LTXAMT,true);
					show(#LVOCDSCPT,true);
					show(#VOCTYPE,true);
					delspace(#CUSTNAME);
					show(#CUSTNAME,true);
					show(#CURCD,true);
					show(#XAVBAL,true);
					show(#PRODNAME,true);
					show(#VOCTYPE,true); 
					show(#CASHFLAG,true); //取消转帐
					show(#VOCDSCPT,true);
					show(#TXAMT,true);
					 #TXAMT=#AMT;
					while(substr(@tota, @i, 4)=='101C')
					{
						 @i = @i + 30;
					}
					@list1=substr(@tota, 0 , @i+1);
					@list2=substr(@tota, @i, len(@tota)-@i);
					setsel(#VOCDSCPT,@list1,'o5t20o1v2o2');
					setsel(#VOCTYPE,@list2,'o5t20o1v3o2');
				  $TXNO='2205';
					//#VOCDSCPT='01';
					//show(#VOCDSCPT,true);
					showblock('ACTINFO',true,true); //改为BLOCK形式	
				 }"

