TRANSACTION.COMMSEND:
"{
    initfd();
    $MSGTYPE='03001';
	commsend_001();
	$FD12='4922';
	$FD16='9986';
	$FD25=#NAME;
    $FD45=#MDATE;
    $FD48=#WDAYS;
    $FD60=#PNNO;
    $FD62=#PANO;
    $FD68=#LIND;
    $FD70=#ACTYPE;
    $FD84=getitems('#PINT');
    $FD90=#NOTYPE;
    $FD100=getitems('#TINT#AMT');
    callserver();
	if($FD12!='0000'){
		showmsg(geterror());
		return(false);
	}
	$FD16 = '4922';
  	$MSGTYPE='03001';
}"

ITEM.ONLOSTFOCUS:
 "{
    	commsend_001();
 		$FD16='9860';
		$FD62=#PANO;
		callserver();
		if($FD12!='0000'){
			showmsg(geterror());
			return(false);
		}
		#NAME=$FD25;
		#MDATE=$FD45;
		#WDAYS=$FD48;
		#PNNO=$FD60;
//		#ACTYPE=$FD67;
		#LIND=$FD68;
		setitems('#PINT',$FD84);
		#NOTYPE=$FD90;
        setitems('#TINT#AMT',$FD100);

		show(#ACTYPE,true);
		show(#NAME,true);
		show(#PINT,true);
		show(#LIND,true);
		show(#WDAYS,true);
		show(#TINT,true);
		show(#NOTYPE,true);
		show(#PNNO,true);
		show(#AMT,true);
		show(#MDATE,true);
 }"

