ITEM.ONLOSTFOCUS:
"{
		dim @tota as string;
		dim @selid as number;
		dim @projectid as string;
		dim @taxorgcode as string;
		dim @tranno as string;
		dim @entrustdate as string;
		dim @cfxno as string;
		dim @filename as string;
		dim @flag as number;
		initfd();
		commsend_001();
		$FD52=#TAXORGCODE;	
		$FD44=#ENTRUSTDATE;
		$FD91=#TXBRNO;
		$FD36=#CFXNO;
		$FD78=#TRANO;
		$FD72='1';
		$FD16=$TXNO;
		//@tota=callserver();
		@tota=calltips();
		if($FD12!='0000'){
			showmsg(geterror());
			close();
			return(false);
		}
		if(autolist(@tota)){
			@selid=list_work();
		}
		@projectid=list_gettext(@selid,5);	
		//showmsg(@projectid);
		@filename='CFX_SMDTL.XML';
		@flag=msgbox('是否查询税目?','提示','yes|no');
		if(@flag==0){
			open(@filename,'#TX_BR_NO='+#TXBRNO+';#TAX_ORG_CODE='+#TAXORGCODE+';#ENTRUST_DATE='+#ENTRUSTDATE+';#CFX_NO='+#CFXNO+';#TRA_NO='+#TRANO+';#PROJECT_ID='+@projectid+';');
			return(true);
		}if(@flag==1){
			close();
			return(true);
		}
	}"
