ITEM.ONLOSTFOCUS:
"{
	if(delspace(#AUTHNUM)!='' and delspace(#AUTHNAME)!=''){
		showmsg('授权号和授权人只能输入其中的一个');
		return(false);
	}
  }"

ITEM.ONCLICK:
"{
	if(showscr('../tmp/'+#WSSRNO+'.scr')){
	}else{
		showmsg('没有找到交易流水');
		goitem(#SHOW,false);
	}
  }"

"{
	if(delspace(#AUTHNUM)!='' and delspace(#AUTHNAME)!=''){
		showmsg('授权号和授权人只能输入其中的一个');
		return(false);
	}
	if(loadfds('../tmp/'+#WSSRNO+'.fds')){
		$FD14='9'+#WSSRNO;
		if(delspace(#AUTHNUM)!=''){
			$FD9='@@@'+#AUTHNUM;
		}else{
			$FD9=#AUTHNAME+#AUTHPWD;
		}
		if($TLRNO==#AUTHNAME){
			showmsg('不能对自己授权!');
			goitem(#OK,false);
			return(true);
		}
		submit();
	}else{
		showmsg('没有找到需授权的交易');
		goitem(#OK,false);
	}
  }"

TRANSACTION.LASTWORK:
"{
	showmsg('交易成功,如需打印请到原交易按流水号['+$FD4+']补打!');
}"
