ITEM.ONLOSTFOCUS:
			"{
			    dim @txamt as string;
		      initfd();
					commsend_001();
					$FD16='8013';
					$FD30=#CARDNO;
					$FD43='';
					callagn();
					if( $FD12 != '0000')
				  {
					  showmsg(geterror());
					  return(false);
          }
         @txamt=$FD40;
         showmsg('交易金额：'+@txamt); 
			   
			}"

