ITEM.ONGOTFOCUS:
"{
	dim @liststr as string;
	dim @selid   as number;
	dim @idx     as number;
	dim @flag    as string;
	@flag = '';

	list_init(11, 32, 10, 17);
	list_setcolcnt(2);
	list_setcol(0, '  开  通', 9);
	list_setcol(1, '     名    称', 20);
	list_additem('', '全选/取消', '');
	list_additem('', '  确定', '');
	if (substr(#SvcClass, 0, 1) == '0')
	{
	list_additem('', '', '人民币单位结算');
	}
	else
	{
	list_additem('', '    √', '人民币单位结算');
	}
	if (substr(#SvcClass, 1, 1) == '0')
	{
	list_additem('', '', '人民币个人结算');
	}
	else
	{
	list_additem('', '    √', '人民币个人结算');
	}
	if (substr(#SvcClass, 2, 1) == '0')
	{
	list_additem('', '', '外币结算业务');
	}
	else
	{
	list_additem('', '    √', '外币结算业务');
	}
	if (substr(#SvcClass, 3, 1) == '0')
	{
	   list_additem('', '', '人民币批量借贷记');
	}
	else
	{
	list_additem('', '    √', '人民币批量借贷记');
	}
	while (true)
	{
	      @selid = list_work();
	      if ((@selid < 0) or (@selid == 1))
	      {
	         @idx = 2;
	         #SvcClass = '';
          	while (@idx < 6)
          	{
	                if (list_gettext(@idx, 0) == '')
          	      {
                   	  #SvcClass = #SvcClass+'0';
	                }
	                else
	                {
	                    #SvcClass = #SvcClass+'1';
	                }
	                @idx = @idx + 1;
	          }         
	          list_del();
	          refresh();
	          return(true);
         }
	       if (@selid == 0)
	       {/*全选*/
	            if (@flag == '')
	            {
	                @flag = '    √';
              }
	            else
	            {
	                 @flag = '';
	            }
	            @idx = 2;
	            while (@idx < 6)
	            {
	                 list_settext(@idx, 0, @flag);
	                 @idx = @idx + 1;
	            }
	       }
	       else
	       {
	             if(list_gettext(@selid, 0) == '')
	             {
	                  list_settext(@selid, 0, '    √');
	             }
	             else
	             {
	                  list_settext(@selid, 0, '');
	             }
	       }
	   }
}"

