		$FD99=#ExchFlag;
		$FD39=#PresBrc;
		$FD40=#AcptBrc;
		$FD44=#SvcClass;
		$FD45=#Region;
		$FD119=#LinkMan;
		$FD58=#BankAlias;
		$FD62=#Telephone;

		$FD66=#OperType; 
		$FD95=#arealist;
		$FD67=#State;
		callagnpbp();
		if($FD12 != '0000')
		{
				showmsg(geterror());
				return(false);
		}

		//生成核心流水-start
		dim @bak_fd81 as string;
		@bak_fd81=$FD81;
		$FD81=#TXNAME;
		gethxtraceno();
		$FD81=@bak_fd81;
		//生成核心流水-end
		runoutput();
		rerun();
		refresh();
	}"

