ITEM.ONCLICK:
 		"{ 		 

			dim @buffer as string; 
			initfd();
			commsend_001();
			$FD16 = '2310';
			$FD18 = 'pbp';
			$FD28 = 'sxps';
			$FD95 = #Month;
			$FD58 = #BRNO;
			callagnpbp();

			if($FD12!='0000')
			{
				  showmsg(geterror());
				  return(false);
			}
			#Ttlcnt = val($FD55);
			#Ttlamt = val($FD39);
			@buffer=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
			if(@buffer == '')
			{
				showmsg('未找到记录!');
				return(false);
			}

			autolist('~'+@buffer);
			runoutput();
			#DOLIST='1';
			goitem('#DOLIST');
			return(true);
			rerun();
			refresh();

 		}"

ITEM.ONLOSTFOCUS:
"{
	dim @selid as number;
	if(#DOLIST=='1')
	{
		@selid=list_work();     
		if(@selid < 0)       
		{                     
			list_del();            
			rerun();               
			return(true);          
		}           
		@selid=list_getselid();
		if(@selid < 0)
		{
			list_del();
			rerun();
			return(true);
		}

		runoutput();
		showall();
		rerun();
		refresh();
	}
	#DOLIST='';
	list_del();
}"

RESPONSE.PRINT:
"{       
	PrintTxHead();
	//list_print();
	if (#BRNO != '')
	{
		print(5, 25,'开户机构:[20]',#BRNO);
	}
	print(7, 25,'月份选择:[20]', getseltitle(#Month,#Month));
	print(9, 25,'汇总笔数:[20]',#Ttlcnt);
	print(11, 25,'汇总金额:[20]',#Ttlamt);
	PrintTxTail();
}"
