ITEM.ONLOSTFOCUS:
"{
	dim @selid as number;
	if(#DOLIST=='1')
	{
		@selid=list_work();     
		if(@selid < 0)       
		{                     
			list_del();            
			rerun();               
			return(true);          
		}           
		@selid=list_getselid();
		if(@selid < 0)
		{
			list_del();
			rerun();
			return(true);
		}
	}
	#DOLIST='';
	list_del();
}"

ITEM.ONCLICK:
 "{
 	dim @buffer as string;

	initfd();
	commsend_001();
	$FD16 = '2046';
	$FD18 = 'pbp';
	$FD33 = #XTCKH;
	@buffer = callagnpbp();
	@buffer=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}

	if(@buffer == '')
	{
		showmsg('未找到记录!');
		return(false);
	}

	autolist('~' + @buffer);
	#DOLIST='1';
	goitem('#DOLIST');

	return(true);
  }"
