ITEM.ONLOSTFOCUS:
"{
		dim @selid as number;
		if(#DOLIST=='1')
		{
			@selid=list_work();     
			if(@selid < 0)       
			{                     
				list_del();            
				rerun();               
				return(true);          
			}           
			@selid=list_getselid();
			if(@selid < 0)
			{
				list_del();
				rerun();
				return(true);
			}
		}
		#DOLIST='';
		list_del();		
}"

ITEM.ONCLICK:
 		"{
 				
				initfd();
				commsend_001();
				$FD16 = '2047';
				$FD18 = 'pbp';
				$FD44 = #QryNum;
				$FD61 = #QryDt;
				dim @rcvbuffer as string;
				
				@rcvbuffer = callagnpbp();
				@rcvbuffer = getfiletxt('../tmp/'+$KINBR+$TTYNAME);
				if($FD12!='0000')
				{
						showmsg(geterror());
						return(false);
				}
					
				dim @filename1 as string;
				dim @ret as number;
					
				@filename1 = '../tmp/'+$KINBR+$HDATE;
				
				@ret = savefiletxt(@filename1, @rcvbuffer);
				if(@ret < 0){
					showmsg('查询失败！');
					return(false);
				}
				
				autolist('~'+@rcvbuffer);
				#DOLIST='1';
				goitem('#DOLIST');
 		}"
