ITEM.ONLOSTFOCUS:
"{
	dim @selid as number;
	if(#DOLIST=='1')
	{
		@selid=list_work();     
		if(@selid < 0)       
		{                     
			list_del();            
			rerun();               
			return(true);          
		}           
		@selid=list_getselid();
		if(@selid < 0)
		{
			list_del();
			rerun();
			return(true);
		}
	}
	#DOLIST='';
	list_del();
}"

