	$FD102 = #Addr;	
	$FD54 = #PostCode;	
	$FD112 = #TelNum;	
	$FD44 = #EffDate;
	$FD45 = #CanclDate;

	dim @rcvbuffer as string;
	dim @filename1 as string;
	dim @ret as number;

	callagnpbp();
	@rcvbuffer=getfiletxt('../tmp/'+$KINBR+$TTYNAME);


	if(@rcvbuffer =='')
	{
		showmsg('读取回传文件失败！');
		return(false);
	}

	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}


	autolist('~'+@rcvbuffer);
	#DOLIST='1';
	goitem('#DOLIST');

	return(true);

}"

