ITEM.ONCLICK:
"{
	dim @buffer as string;
	initfd();
	commsend_001();
	$FD16 = '2318';
	$FD18 = 'pbp';
	$FD28 = 'sxps';
	$FD44 = #Workdt;
	$FD52 =	#Workround;
	$FD45 = #Sttlmdt;
	$FD58 =	#Sttlmrnd;	
	call('rm -rf '+'../tmp/'+$KINBR+$TTYNAME);	
	callagnpbp();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	@buffer=getfiletxt('../tmp/'+$KINBR+$TTYNAME);
	showmsg('[' + delspace(@buffer) + ']');
	if(delspace(@buffer) == '')
	{
		showmsg('未找到失败记录!');
		runoutput();
		rerun();
		refresh();
	}
	else if(autolist('~'+@buffer))
	{ 					 
		list_show();
		runoutput();
		rerun();
		refresh();
	}
	   			
	
}"

RESPONSE.PRINT:
"{
		PrintTxHead();
		print(5, 20, '工作日期： [8]	工作场次：[20]',#Workdt,#Workround);
		print(7, 20, '清算日期： [8]	清算场次：[20]',#Sttlmdt,#Sttlmrnd);
		print(9, 20, '对账结果： [60]',$FD13);
		PrintTxTail();
}"
