ITEM.ONCLICK:
"{
	initfd();
	commsend_001();
	$FD16 = '2319';
	$FD18 = 'pbp';
	$FD28 = 'sxps';
	$FD44 = #Workdt;
	$FD52 =	#Workround;
	$FD45 = #Sttlmdt;
	$FD58 =	#Sttlmrnd;		
	callagnpbp();
	if($FD12!='0000')
	{
		showmsg(geterror());
		return(false);
	}
	//生成核心流水-start
	dim @bak_fd81 as string;
	@bak_fd81=$FD81;
	$FD81=#TXNAME;
	gethxtraceno();
	$FD81=@bak_fd81;
	//生成核心流水-end	
	runoutput();
	rerun();
	refresh();
}"

