ITEM.ONCLICK:
	"{
                initfd();
                commsend_001();
                $FD18 = 'pbp';
                $FD28 = 'sxps';
                $FD66 = #Mode;
               	$FD39=itoa(#Txamt*100, '%16.0f');
                $FD16 = '2322';
                callagnpbp();
                if($FD12 != '0000')
		{
			showmsg(geterror());
			return(false);
		}
		if (#Mode == '2')
		{
			#Txamt = $FD43;
		}
		runoutput();
		rerun();
		refresh();
	}"

RESPONSE.PRINT:
	"{
		PrintTxHead();
		if (#Mode == '2')
		{	 
   			print(10,30,'滞留金额当前额度:[60]',ltrim(itoa(#Txamt,'%.2f')));
		}
		else
		{
			print(8,30,'滞留金额设置成功');
			print(10,30,'滞留金额当前额度:[60]',ltrim(itoa(#Txamt,'%.2f')));
		}	
		PrintTxTail();	
	}"
