ITEM.ONLOSTFOCUS:
"{
      if(#DQFS=='1')
      {
        enable(#ACTNO1,false);
        enable(#ACTNO2,true);
        show(#ACTNO1,false);
        show(#ACTNO2,true);
      }
      if(#DQFS=='0')
      {
        enable(#ACTNO1,true);
        enable(#ACTNO2,false);
        show(#ACTNO1,true);
        show(#ACTNO2,false);
      }
      refresh();
    }"

"{
  //IC卡bin=621780  ,磁条卡bin=621223
  #MSR2BAK=$MSR2;
  #MSR3BAK=$MSR3;
  $MSR2='';
  $MSR3='';

  //核心查询已绑定卡户名
  dim @info as string;
  dim @tmpacid as string;
  #AC_NAME='';
  refresh();

  initfd();
  commsend_001();
  $FD16='9598';
  $FD123='select ac_id  from mdm_ac_rel where ac_no='+chr(39)+#AC_NO+chr(39);
  @info=callserver();
  if($FD12!='0000')
  {
      showmsg(geterror());
      return(false);
  }
  if(@info=='')
  {
      showmsg('账号不存在,请核对后重新输入!!');
      return(false);
  }
  @tmpacid=delspace(strfld(@info,'|',0));
  initfd();
  commsend_001();
  $FD16='9598';
  @info='';
  $FD123='select name from dd_mst where ac_id='+chr(39)+@tmpacid+chr(39);
  @info=callserver();
  if($FD12!='0000')
  {
      showmsg(geterror());
      return(false);
  }
  #AC_NAME=delspace(strfld(@info,'|',0));
}"

ITEM.ONCLICK:
"{
      initfd();
      commsend_001();

      $FD16='9040';             //平台用户绑定查询接口
      $FD60='AGENT';             //平台交易类型
      $FD31=#AC_NO;             //查询银行卡

      callagnpt();
      if($FD12!='0000'){
        showmsg(geterror());
        rerun();
        return(false);
      }

      #USER_NO=delspace($FD30);  //用户代码
      #BI_DATE=delspace($FD28);  //用户绑定日期
      #BI_TEL=delspace($FD26);   //绑定操作柜员
      #AG_STS=delspace($FD70);   //代扣签/解约状态
      #AG_DATE=delspace($FD29);  //代扣签/解约日期

      if(#AG_STS=='0'){
        #AG_Q_DATE=delspace($FD29);  //代扣签约日期
        #AG_Q_TEL=delspace($FD27);   //代扣签约操作柜员
      }else{
        #AG_J_DATE=delspace($FD29);  //代扣解约日期
        #AG_J_TEL=delspace($FD27);   //代扣解约操作柜员
      }

      if(#AG_STS=='1' and #AG_DATE=='') //第一次绑定时默认解约不显示
      {
          show(#LAG_Q_DATE,false);
          show(#AG_Q_DATE,false);
          show(#LAG_Q_TEL,false);
          show(#AG_Q_TEL,false);
          show(#LAG_J_DATE,false);
          show(#AG_J_DATE,false);
          show(#LAG_J_TEL,false);
          show(#AG_J_TEL,false);
      }
      else
      {
        if(#AG_STS=='0')
        {
          show(#LAG_Q_DATE,true);
          show(#AG_Q_DATE,true);
          show(#LAG_Q_TEL,true);
          show(#AG_Q_TEL,true);
        }
        else
        {
          show(#LAG_J_DATE,true);
          show(#AG_J_DATE,true);
          show(#LAG_J_TEL,true);
          show(#AG_J_TEL,true);
        }
      }

      initfd();
      commsend_001();
      $FD16='1010';                  //平台用户查询接口
      $FD60='RQDS';                  //平台交易类型
      $FD30=#USER_NO;
      callagnpt();
      if($FD12!='0000')
      {
        showmsg(geterror());
        return(false);
      }
      #YHNAME=delspace($FD25);       //用户姓名
      #ADDRESS=delspace($FD76);      //用户单位及地址

      showmsg('查询成功!!!');
      runoutput();
      rerun();
    }"

RESPONSE.PRINT:
"{
  print(3,25,'燃气用户绑定/签约查询成功');
  print(5,20,'绑定卡号：[19]        绑定卡户名：[60]',#AC_NO,#AC_NAME);
  print(6,20,'用户代码：[6]                     用户名：[60]',#USER_NO,#YHNAME);
  print(7,20,'地    址：[64]',#ADDRESS);
  print(8,20,'用户绑定日期：[8]               绑定操作柜员：[4]',#BI_DATE,#BI_TEL);

  if(#AG_STS=='0')
  {
    print(9,20,'代扣签约日期：[8]               签约操作柜员：[4]',#AG_Q_DATE,#AG_Q_TEL);
  }
  if(#AG_STS=='1' and #AG_DATE!='')
  {
    print(9,20,'代扣解约日期：[8]               解约操作柜员：[4]',#AG_J_DATE,#AG_J_TEL);
  }
}"

